mapper.select:
select board_num, title, writer, DATE_FORMAT(date, '%Y-%m-%d') as date, content, cbnNum
from notice


        AND DATE_FORMAT(date, '%Y-%m-%d')  >=  #{start}


        AND DATE_FORMAT(date, '%Y-%m-%d')  <=  #{end}


        AND title LIKE CONCAT('%',#{title},'%')

mapper.update:
update notice
set cbnNum = cbnNum + 1
where ntc_num = #{ntcNum}

mapper.select:
select title, writer, DATE_FORMAT(date, '%Y-%m-%d') as date, content
from board
WHERE board_num = #{boardNum}
