mapper.select:
select board_num, title, writer, DATE_FORMAT(date, '%Y-%m-%d') as date, content, cbn_num
from board

        del_yn = 'n'

        AND DATE_FORMAT(date, '%Y-%m-%d')  >=  #{start}


        AND DATE_FORMAT(date, '%Y-%m-%d')  <=  #{end}


        AND title LIKE CONCAT('%',#{title},'%')

mapper.update:
update board
set cbn_num = cbn_num + 1
where board_num = #{boardNum}

mapper.select:
select title, writer, password, DATE_FORMAT(date, '%Y-%m-%d') as date, content
from board
WHERE board_num = #{boardNum}
