mapper.select:
select emp_table.hire_date, emp_table.auth_code, emp_table.emp_id, emp_table.salary, emp_table.emp_name, substring(emp_table.idp_num, 1,6) as birth, hdqrt_code.hq_name, dep_code.dep_name, pstn_code.job_name, auth_table.auth_name from emp_table left join hdqrt_code on emp_table.hq_code = hdqrt_code.hq_code left join dep_code on emp_table.dep_no= dep_code.dep_no left join pstn_code on emp_table.job_code= pstn_code.job_code left join auth_table on emp_table.auth_code= auth_table.auth_code
AND emp_table.hire_date >= #{start}
AND emp_table.hire_date <= #{end}
AND hdqrt_code.hq_name = #{hqName}
AND dep_code.dep_name = #{depName}
AND pstn_code.job_name = #{jobName}
AND emp_table.emp_name = #{empName}

select substring(emp_table.idp_num, 1,6) as birth, emp_table.emp_id, emp_table.wage, emp_table.emp_name, pstn_code.job_name, hdqrt_code.hq_name, dep_code.dep_name, payrollhistory.serial_no, payrollhistory.tax, payrollhistory.Meal_exp, payrollhistory.trn_exp, payrollhistory.wel_exp, payrollhistory.paymdate from emp_table left join hdqrt_code on emp_table.hq_code = hdqrt_code.hq_code left join dep_code on emp_table.dep_no= dep_code.dep_no left join payrollhistory on emp_table.emp_id= payrollhistory.emp_id left join pstn_code on emp_table.job_code= pstn_code.job_code
AND YEAR(payrollhistory.paymdate) = #{PayYear}
AND MONTH(payrollhistory.paymdate) = #{PayMonth}
AND emp_table.emp_id = #{emNum}

select substring(emp_table.idp_num, 1,6) as birth, emp_table.emp_id, emp_table.wage, emp_table.emp_name, pstn_code.job_name, hdqrt_code.hq_name, dep_code.dep_name, payrollhistory.serial_no, payrollhistory.tax, payrollhistory.Meal_exp, payrollhistory.trn_exp, payrollhistory.wel_exp, payrollhistory.paymdate from emp_table left join hdqrt_code on emp_table.hq_code = hdqrt_code.hq_code left join dep_code on emp_table.dep_no= dep_code.dep_no left join payrollhistory on emp_table.emp_id= payrollhistory.emp_id left join pstn_code on emp_table.job_code= pstn_code.job_code
AND emp_table.emp_id = #{empId}
order by payrollhistory.paymdate desc;

mapper.update:
UPDATE emp_table
SET job_code = #{jobCode}
,   salary = #{salary}
WHERE emp_id = #{empId}
